import { useState, useRef } from "react";

const SimpleInput = (props) => {
  const nameInputRef = useRef();
  const [enteredName, setEnteredName] = useState("");
  const [enteredNameIsValid, setEnteredNameIsValid] = useState(false);
  const [enteredNameIsTouched, setEnteredNameIsTouched] = useState(false);

  const inputNameIsInvalid = ! enteredNameIsValid && enteredNameIsTouched;
  const nameInputChangeHandler = (event) => {
    setEnteredName(event.target.value);
    if (event.target.value.trim() === '') {
      setEnteredNameIsValid(false);
      return;
    }
    setEnteredNameIsValid(true);
  };
  const nameInputBlurHandler = (event) => {
    setEnteredNameIsTouched(true)
    if (enteredName.trim() === '') {
      setEnteredNameIsValid(false);
      return;
    }
  }
  const submitFormHandler = (event) => {
    event.preventDefault();
    setEnteredNameIsTouched(true);
    if (enteredName.trim() === '') {
      setEnteredNameIsValid(false);
      return;
    }
    console.log(enteredName);
    // const enteredNameValue = nameInputRef.current.value; ----> not Ideal
    // console.log(enteredNameValue);
    setEnteredNameIsValid(true);
    setEnteredName("");
  };
  const nameInputClasses = !inputNameIsInvalid ? 'form-control':'form-control invalid'
  return (
    <form onSubmit={submitFormHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          value={enteredName}
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
        />
      </div>
      {inputNameIsInvalid && <p className="error-text">Name must not be empty</p>}
      <div className="form-actions">
        <button>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
