import { useState, useEffect } from "react";
import useInput from "../hooks/use-input";

const SimpleInput = (props) => {
  const {
    value: enteredName,
    isValid:nameIsValid,
    hasError: inputNameIsInvalid,
    valueChangeHandler: nameInputChangeHandler,
    valueBlurHandler: nameInputBlurHandler,
    reset:resetName
  } = useInput(((value) => value.trim() !== ""));

  const {
    value: enteredEmail,
    iSValid: emailIsValid,
    hasError: inputEmailIsInvalid,
    valueChangeHandler: emailInputChangeHandler,
    valueBlurHandler: emailInputBlurHandler,
    reset:resetEmail
  } = useInput((value) => value.includes("@"));
  let formIsValid = false;

  if (nameIsValid && emailIsValid) {
    formIsValid = true;
  }

  const nameInputClasses = !inputNameIsInvalid
    ? "form-control"
    : "form-control invalid";

  const emailInputClasses = !inputEmailIsInvalid
    ? "form-control"
    : "form-control invalid";

  const submitFormHandler = (event) => {
    event.preventDefault();
    if(formIsValid)
    return;
    resetName();
    resetEmail();
  };

  return (
    <form onSubmit={submitFormHandler}>
      <div className={nameInputClasses}>
        <label htmlFor="name">Your Name</label>
        <input
          type="text"
          id="name"
          value={enteredName}
          onChange={nameInputChangeHandler}
          onBlur={nameInputBlurHandler}
        />
      </div>
      {inputNameIsInvalid && (
        <p className="error-text">Name must not be empty</p>
      )}
      <div className={emailInputClasses}>
        <label htmlFor="email">Your Email</label>
        <input
          type="text"
          id="email"
          value={enteredEmail}
          onChange={emailInputChangeHandler}
          onBlur={emailInputBlurHandler}
        />
      </div>
      {inputEmailIsInvalid && <p className="error-text">Enter a valid email</p>}
      <div className="form-actions">
        <button disabled={!formIsValid}>Submit</button>
      </div>
    </form>
  );
};

export default SimpleInput;
